INSERT INTO wp_posts (post_title, post_content, post_name, post_date, post_date_gmt, post_modified, post_modified_gmt, post_author, post_status, post_type) VALUES ('AGELESS, TIMELESS, ETERNAL: WHAT LIGHT THROUGH YONDER WINDOW BREAKS?', '
<p class=\"showTitle\">AGELESS, TIMELESS, ETERNAL: WHAT LIGHT THROUGH YONDER WINDOW BREAKS?</p>
<p>Text by William Shakespeare<br/>
<br/>Choreographed by Kristin Sudeikis &amp; Rachel Warren<br/>
<br/>Produced by POV Artists &amp; Collide Productions</p>
<p>Told through the timeless words of Shakespeare and the choreography of Kristin Sudeikis &amp; Rachel Warren, this classic love story will be told through the eyes of star-crossed lovers in their 20\'s, 40\'s &amp; 60\'s to make clear that the possibilities of love are there for all of us eternally - if we are open to it.</p>', 'AGELESS_TIMELESS_ETERNAL_WHAT_LIGHT_THROUGH_YONDER_WINDOW_BREAKS', '2018-05-06 15:05:59', '2018-05-06 15:05:59', '2018-05-06 15:05:59', '2018-05-06 15:05:59', 1, 'publish','performancearchive'); 
