INSERT INTO wp_posts (post_title, post_content, post_name, post_date, post_date_gmt, post_modified, post_modified_gmt, post_author, post_status, post_type) VALUES ('Star Wars...Entirely from Memory', '<img align=\"Right\" alt=\"\" hspace=\"5\" src=\"http://localhost:3000/wp-content/themes/wildproject/images/performances/2017-Star-Wars.jpg\" vspace=\"5\"/>
